import React from "react";

const TextareaAutoResize = () => {
  return (
    <div className="text-center">
      <textarea
        name=""
        className="w-full max-w-[400px] p-5 rounded-lg border border-gray-300 resize-none"
        id=""
        cols="30"
        rows="10"
      ></textarea>
    </div>
  );
};

export default TextareaAutoResize;
