import React from "react";

const TextareaAutoResize = () => {
  return (
    <div className="p-5">
      <textarea
        className="w-full focus:border-green-300 max-w-[400px] p-5 rounded-lg border border-gray-300 resize-none"
        // style="height:auto;"
        placeholder="Please enter your content..."
      ></textarea>
    </div>
  );
};

export default TextareaAutoResize;
