import { Fragment, useEffect } from "react";
import { useRef, useState } from "react";
import "./App.scss";
import Blogs from "./components/Blogs";
import { ErrorBoundary } from "react-error-boundary";

// import Button from "./components/button/Button";
import Cart from "./components/cart/Cart";
import CartTailwind from "./components/cart/CartTailwin";
import Counter from "./components/counter/Counter";
import Dropdown from "./components/Dropdown";
import DropdownPortal from "./components/DropdownPortal";
import InputForm from "./components/form/InputForm";
import SignUpForm from "./components/form/SignUpForm";
import SignUpFormFinal from "./components/form/SignUpFormFinal";
import SignUpFormHook from "./components/form/SignUpFormHook";
import SignUpFormV2 from "./components/form/SignUpFormV2";
import Header from "./components/Header";
import Input from "./components/Input";
import Modal from "./components/modal/Modal";
import MovieSearchApp from "./components/MovieSearchApp";
import HackerNews from "./components/news/HackerNews";
import HackerNewsWithHook from "./components/news/HackerNewsWithHook";
import HackerNewsWithReducer from "./components/news/HackerNewsWithReducer";
import Photo from "./components/photo/Photo";
import SlideBarMenu from "./components/SlideBarMenu";
import StopWatch from "./components/StopWatch";
import TextareaAutoResize from "./components/TextareaAutoResize";
import Timer from "./components/Timer";
import Tooltip from "./components/tooltip/Tooltip";
import useClickOutSide from "./hooks/useClickOutSide";
import GameWithReducer from "./components/tictactoe/GameWithReducer";
import Portal from "./components/Portal";
import ModalBase from "./components/modal/ModalBase";
import ModalAdvanced from "./components/modal/ModalAdvanced";
// import DoubleCounter from "./components/counter/DoubleCounter";
// import Game from "./components/tictactoe/Game";
// import Counter from "./components/counter/Counter";
// import Toggle from "./components/toggle/Toggle";
// import YoutubeList from "./components/youtube/YoutubeList";

/*

  * JSX:javascript XML:
  - render ra dom
  - cho phép viết code dễ dàng, dễ dọc, dễ bảo trì

  * ES6

  * babel

  --------------------------------
    * element = <div id="root">Hello World</div>: jsx
    * element = react.createElement('div', {id:'root'}, 'Hello world')
    * function createElement(elementType, props, ...children)
    * ElementType : 'div', 'p', 'span'
    * props : className, id, src , alt
    * ...children:
      * element2={<div>
      * <span>Hello </span>
      * <span>World</span> 
      * </div>}  
      * Element2 = react.createElement('div',{
      * children:[
      * reate.createElement('span', null, 'Hello'),
      * ' ',
      * reate.createElement('span', null, 'Hello'),
      * ]
      * })
  * components : chia nhỏ mọi thứ để dễ quản lý
  * props: properties 
*/
function ErrorFallback({ error, resetErrorBoundary }) {
  return (
    <div role="alert">
      <p>Something went wrong:</p>
      <pre>{error.message}</pre>
      <button onClick={resetErrorBoundary}>Try again</button>
    </div>
  );
}
function App() {
  const [showModal, setShowModal] = useState(false);
  const [openModalBase, setOpenModalBase] = useState(false);
  const [openModal, setOpenModal] = useState(false);

  return (
    <Fragment>
      {/* <Modal open={true} handleClose={() => {}}></Modal> */}
      {/* <Portal vis
      ible></Portal> */}
      <button
        className="p-5 mr-5 text-center text-white bg-blue-500 rounded-lg"
        onClick={() => setOpenModalBase(true)} //sai đây nè thấy rồi
      >
        Open modal base
      </button>
      <button
        className="p-5 text-center text-white bg-blue-500 rounded-lg"
        onClick={() => setOpenModal(true)} //sai đây nè thấy rồi
      >
        Open modal
      </button>
      <ModalBase
        visible={openModalBase}
        onClose={() => setOpenModalBase(false)}
      >
        <div className="w-full p-10 bg-white rounded-lg max-w-[320px]">
          Lorem ipsum dolor, sit amet consectetur adipisicing elit. Aut
          recusandae laudantium consequuntur deserunt possimus minus suscipit
          saepe at, vero magni ab, aspernatur illum error rerum quis? Saepe
          voluptate quia tenetur?
        </div>
      </ModalBase>
      <ModalAdvanced
        visible={true}
        onClose={() => setOpenModal(false)}
        bodyClassName="w-full max-w-[400px] bg-white p-10 rounded-lg"
      >
        <div className="relative z-10 w-full p-10 bg-white rounded-lg modal-content max-w-[482px]">
          <span
            className="absolute top-0 right-0 flex items-center justify-center w-10 h-10 p-1 bg-white rounded-full cursor-pointer -translate-y-2/4 translate-x-2/4"
            onClick={() => setOpenModal(false)}
          >
            <svg
              width="14"
              height="14"
              viewBox="0 0 14 14"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M8.225 7L13.7375 1.4875C14.0875 1.1375 14.0875 0.6125 13.7375 0.2625C13.3875 -0.0875 12.8625 -0.0875 12.5125 0.2625L7 5.775L1.4875 0.2625C1.1375 -0.0875 0.6125 -0.0875 0.2625 0.2625C-0.0874998 0.6125 -0.0874998 1.1375 0.2625 1.4875L5.775 7L0.2625 12.5125C0.0875002 12.6875 0 12.8625 0 13.125C0 13.65 0.35 14 0.875 14C1.1375 14 1.3125 13.9125 1.4875 13.7375L7 8.225L12.5125 13.7375C12.6875 13.9125 12.8625 14 13.125 14C13.3875 14 13.5625 13.9125 13.7375 13.7375C14.0875 13.3875 14.0875 12.8625 13.7375 12.5125L8.225 7Z"
                fill="#84878B"
              />
            </svg>
          </span>
          <h2 className="mb-5 text-4xl font-medium text-center text-black">
            Welcome Back!
          </h2>
          <div className="flex flex-col gap-3 mb-5">
            <label htmlFor="email" className="text-sm cursor-pointer">
              Email address
            </label>
            <input
              type="email"
              className="w-full text-sm leading-normal bg-[#E7ECF3] rounded-lg p-4"
              placeholder="Enter your email"
            />
          </div>
          <div className="flex flex-col gap-3 mb-5">
            <label htmlFor="password" className="text-sm cursor-pointer">
              Password
            </label>
            <input
              type="password"
              className="w-full text-sm leading-normal bg-[#E7ECF3] rounded-lg p-4"
              placeholder="Enter your password"
            />
          </div>
          <button className="w-full p-4 text-base font-semibold text-white bg-[#316BFF] rounded-lg">
            Sign in
          </button>
        </div>
      </ModalAdvanced>
    </Fragment>
  );
}

export default App;
