import "./App.css";
import { YoutubeData } from "./data";
/*

  * JSX:javascript XML:
  - render ra dom
  - cho phép viết code dễ dàng, dễ dọc, dễ bảo trì

  * ES6

  * babel

  --------------------------------
    * element = <div id="root">Hello World</div>: jsx
    * element = react.createElement('div', {id:'root'}, 'Hello world')
    * function createElement(elementType, props, ...children)
    * ElementType : 'div', 'p', 'span'
    * props : className, id, src , alt
    * ...children:
      * element2={<div>
      * <span>Hello </span>
      * <span>World</span> 
      * </div>}  
      * Element2 = react.createElement('div',{
      * children:[
      * reate.createElement('span', null, 'Hello'),
      * ' ',
      * reate.createElement('span', null, 'Hello'),
      * ]
      * })
  * components : chia nhỏ mọi thứ để dễ quản lý
  * props: properties 
*/
function Feature() {
  return (
    <div class="feature">
      <img src="" alt="" className="feature-img" />
      <h3 className="feature-title">Title</h3>
      <p className="feature-desc">
        Lorem ipsum, dolor sit amet consectetur adipisicing elit. Voluptatum
        animi veritatis error sed in corrupti quae repudiandae beatae? Unde
        repellat culpa eligendi ipsum dolor, molestias saepe distinctio atque
        consequatur in.
      </p>
    </div>
  );
}
// parent component
function App() {
  // const name = "";
  // function fullname(firstName, lastName) {
  //   return `${firstName} ${lastName}`;
  // }
  return (
    <div>
      {/* children component */}
      {/* <Feature></Feature>
      <Feature></Feature>
      <Feature></Feature> */}
      <YoutubeItem
        image="https://plus.unsplash.com/premium_photo-1663840225561-76ecde33143c?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=987&q=80"
        avt="https://images.unsplash.com/photo-1534528741775-53994a69daeb?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=764&q=80"
        title="I am developer "
        author="evondev"
      ></YoutubeItem>
    </div>
  );
  // return (
  //   <div className="App">
  //     <header className="App-header">
  //       <img src={logo} className="App-logo" alt="logo" />
  //       <p>
  //         Edit <code>src/App.js</code> and save to reload.
  //       </p>
  //       <a
  //         className="App-link"
  //         href="https://reactjs.org"
  //         target="_blank"
  //         rel="noopener noreferrer"
  //       >
  //         Learn React
  //       </a>
  //     </header>
  //   </div>
  // );
}

function YoutubeItem(props) {
  console.log(props);
  return (
    <div className="youtube-item">
      <div className="youtube-img">
        <img src={props.image} alt="" />
      </div>
      <div className="youtube-footer">
        <img src={props.avt} alt="" className="youtube-avt" />
        <div className="youtube-info">
          <h3 className="youtube-title">{props.title} </h3>
          <h4 className="youtube-author">{props.author}</h4>
        </div>
      </div>
    </div>
  );
}
export default App;
