import axios from "axios";
import React, { useRef, useState } from "react";

const initialState = {
  hits: [],
  quety: "",
  loading: true,
  errorMessage: "",
  url: "https://hn.algolia.com/api/v1/search?query=''",
};
const hackerNewsReducer = (state, action) => {
  return state;
};
const HackerNewsReducer = () => {
