import React from "react";
import { useAuth } from "../contexts/auth-context";

const Headermain = () => {
  const { user } = useAuth();
  return (
    <div className="flex items-center justify-center p-4 bg-white shadow-md">
      {user ? (
        <div className="flex items-center justify-center">
          <img
            src="https://images.unsplash.com/photo-1426604966848-d7adac402bff?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80"
            className="object-cover w-10 h-10 rounded-full"
            alt=""
          />
          <span className="text-sm font-medium">
            Welcom! <strong>{user.fullname}</strong>
          </span>
        </div>
      ) : (
        <span className="">WELCOM</span>
      )}
    </div>
  );
};

export default Headermain;
