import axios from "axios";
import React, { useReducer, useRef, useState } from "react";

//https://hn.algolia.com/api/v1/search?query=react
const HackerNews = () => {
  const [hits, setHits] = useState([]);
  const [query, setQuery] = useState("react");
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessgage] = useState("");
  const handleFetchData = useRef({});
  handleFetchData.current = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        `https://hn.algolia.com/api/v1/search?query=${query}`
      );
      // console.log(response.data);
      setHits(response.data?.hits || []);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      setErrorMessgage(`The error happend ${error}`);
    }
  };
  const handleUpdateQuery = lodas.debounce((e) => {
    setQuery(e.target.value);
  }, 500);
  React.useEffect(() => {
    handleFetchData.current();
  }, [query]);
  return (
    <div>
      <input
        type="text"
        className="border border-black text-black p-5 "
        value={query}
        onChange={handleUpdateQuery}
      />
      {loading && (
        <div className="loading w-8 h-8  rounded-full animate-spin border border-green-400 border-4 border-r-4 border-r-transparent"></div>
      )}
      {!loading && errorMessage && <p>{errorMessage}</p>}
      {!loading &&
        hits.length > 0 &&
        hits.map((item, index) => <h3 key={item.title}>{item.title}</h3>)}
    </div>
  );
};

export default HackerNews;
