      {/* <Button>Primary</Button> */}
      {/* <Cart></Cart>
  <div>
    <h2 className="py-3 text-[#ccc]">banhs us</h2>
  </div> */}
      {/* <CartTailwind></CartTailwind> */}
      {/* CartList */}
      {/* <Photo></Photo> */}
      {/* <HackerNews></HackerNews> */}
      {/* <HackerNewsWithReducer></HackerNewsWithReducer> */}
      <StopWatch></StopWatch>
    </div>
  );
};

export default StopWatch;
