import React from "react";
import { useAuth } from "../contexts/auth-context";

const Headermain = () => {
  const { user, setUser } = useAuth();
  return (
    <div className="flex items-center justify-center w-full p-4 bg-white shadow-md">
      {user ? (
        <div className="flex items-center justify-center">
          <img
            src={user.avt}
            className="object-cover w-10 h-10 rounded-full"
            alt=""
          />
          <span className="text-sm font-medium">
            Welcom! <strong>{user.fullName}</strong>
          </span>
        </div>
      ) : (
        <span className="text-sm font-medium ">WELCOM</span>
      )}
      <button
        onClick={() => setUser(null)}
        className="p-2 ml-auto text-black bg-gray-300 rounded-md"
      >
        Sign Out
      </button>
    </div>
  );
};

export default Headermain;
